package com.example.amritha_manoharan.quadratic;import android.app.Activity;import android.content.Context;import android.content.SharedPreferences;import android.graphics.Color;import android.hardware.Sensor;import android.hardware.SensorEventListener;import android.hardware.SensorListener;import android.hardware.SensorManager;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.text.method.ScrollingMovementMethod;import android.view.View;import android.view.inputmethod.EditorInfo;import android.view.inputmethod.InputMethodManager;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;import org.w3c.dom.Text;public class MainActivity extends AppCompatActivity {    EditText ea;    EditText eb;    EditText ec;    TextView nat;    TextView root;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        nat = (TextView)findViewById(R.id.nature);        nat.setText("Enter A,B,C of the Equation");        ea = (EditText)findViewById(R.id.entera);        eb = (EditText)findViewById(R.id.enterb);        ec = (EditText)findViewById(R.id.enterc);        root = (TextView)findViewById(R.id.roots);        root.setMovementMethod(new ScrollingMovementMethod());    }    public void find(View view)    {            String s1, s2, s3;            s1 = ea.getText().toString();            s2 = eb.getText().toString();            s3 = ec.getText().toString();            String ts = "0";            if( s1.equals(ts) ){                nat.setText("Please Check The Value of A");                nat.setTextColor(Color.parseColor("#ff0000"));            }            else {                    if(s1.length() == 0 || s2.length() == 0 || s3.length() == 0){                        nat.setText("Please Enter The Required Fields");                        nat.setTextColor(Color.parseColor("#ff7f50"));                    }                else {                    Double n1, n2, n3;                    n1 = Double.parseDouble(s1);                    n2 = Double.parseDouble(s2);                    n3 = Double.parseDouble(s3);                    nat.setTextColor(Color.parseColor("#468499"));                    Double d = ((n2 * n2) - (4 * n1 * n3));                    if (d == 0) {                        nat.setText("The Roots Are Real and Equal");                        double r1 = -n2 / (2 * n1);                        double r2 = r1;                        root.setText("Root1 = " + Double.toString(r1) + "\nRoot2 = " + Double.toString(r2));                    } else if (d > 0) {                        nat.setText("The Roots Are Real and Distinct");                        Double sqrt = Math.sqrt(d);                        Double r1 = (-n2 + sqrt) / (2 * n1);                        Double r2 = (-n2 - sqrt) / (2 * n1);                        root.setText("Root1 = " + Double.toString(r1) + "\nRoot2 = " + Double.toString(r2));                    } else {                        nat.setText("The Roots Are Imaginary and Distinct");                        Double abs = Math.abs(d);                        Double absqrt = Math.sqrt(abs);                        Double r = (-n2 + absqrt) / (2 * n1);                        Double i = (-n2 - absqrt) / (2 * n1);                        root.setText("Root1 = " + Double.toString(r) + "+i(" + Double.toString(i) + ")\nRoot2 = " + Double.toString(r) + "-i(" + Double.toString(i) + ")");                    }                }            }    }    public void clear(View view)    {        ea.setText("");        eb.setText("");        ec.setText("");        nat.setText("Enter A,B,C of the Equation");        nat.setTextColor(Color.parseColor("#000000"));        root.setText("");    }}